import collections


class Solution(object):
    def longest(self, matrix, row, col, cached):
        max_len = 1

        if row - 1 >= 0 and matrix[row - 1][col] > matrix[row][col]:
            if (row - 1, col) in cached:
                x = cached[(row - 1, col)]
            else:
                x = self.longest(matrix, row - 1, col, cached)

            max_len = max(max_len, 1 + x)

        if col - 1 >= 0 and matrix[row][col - 1] > matrix[row][col]:
            if (row, col - 1) in cached:
                x = cached[(row, col - 1)]
            else:
                x = self.longest(matrix, row, col - 1, cached)

            max_len = max(max_len, 1 + x)

        if row + 1 < len(matrix) and matrix[row + 1][col] > matrix[row][col]:
            if (row + 1, col) in cached:
                x = cached[(row + 1, col)]
            else:
                x = self.longest(matrix, row + 1, col, cached)

            max_len = max(max_len, 1 + x)

        if col + 1 < len(matrix[0]) and matrix[row][col + 1] > matrix[row][col]:
            if (row, col + 1) in cached:
                x = cached[(row, col + 1)]
            else:
                x = self.longest(matrix, row, col + 1, cached)

            max_len = max(max_len, 1 + x)

        cached[(row, col)] = max(cached[(row, col)], max_len)

        return max_len

    def longestIncreasingPath(self, matrix):
        if len(matrix) == 0 or len(matrix[0]) == 0:
            return 0

        cached = collections.defaultdict(int)

        max_len = 1

        for row in range(len(matrix)):
            for col in range(len(matrix[0])):
                max_len = max(max_len, self.longest(matrix, row, col, cached))

        return max_len


